import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/pages/search-majors.css';
import { majorService } from '../services/majorService';

const SearchMajorsPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [majors, setMajors]       = useState([]); // flat list ngành con
  const [allMajors, setAllMajors] = useState([]); // dữ liệu gốc
  const [loading, setLoading]     = useState(true);

  useEffect(() => {
    const fetchMajors = async () => {
      setLoading(true);
      try {
        // majorService.getAll() trả về payload { success, count, data }
        const payload = await majorService.getAll();
        if (payload.success && Array.isArray(payload.data)) {
          // flatten groups → majors, bạn có thể giữ thêm thông tin group nếu cần
          const flatList = payload.data.flatMap(group =>
            (group.majors || []).map(major => ({
              ...major,
              groupId: group.group_id,
              groupName: group.group_name,
            }))
          );
          setAllMajors(flatList);
          setMajors(flatList);
        } else {
          setAllMajors([]);
          setMajors([]);
        }
      } catch (error) {
        console.error('Fetch error:', error);
        setAllMajors([]);
        setMajors([]);
      } finally {
        setLoading(false);
      }
    };
    fetchMajors();
  }, []);

  const handleSearch = e => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setMajors(allMajors);
      return;
    }
    const q = searchQuery.toLowerCase();
    setMajors(allMajors.filter(m =>
      m.major_name.toLowerCase().includes(q) ||
      m.major_code.toLowerCase().includes(q)
    ));
  };

  return (
    <div className="sm-page">
      <section className="search-header">
        <h1>Tìm kiếm ngành học</h1>
        <form className="search-bar" onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Nhập tên hoặc mã ngành..."
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
          />
        </form>
      </section>

      <div className="sm-results-panel">
        {loading
          ? <p>Đang tải dữ liệu...</p>
          : majors.length === 0
            ? <p>Không tìm thấy ngành nào.</p>
            : majors.map(major => (
              <Link
                to={`/major/${major.major_id}`} 
                key={major.major_id}
                className="sm-major-card"
              >
                <div className="sm-major-header">
                  <h4>{major.major_name}</h4>
                  <span className="major-code">
                    Mã ngành: {major.major_code}
                  </span>
                </div>
                <small className="group-name">
                  Nhóm: {major.groupName}
                </small>
              </Link>
            ))
        }
      </div>
    </div>
  );
};

export default SearchMajorsPage;
