import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getAdmissionScores } from '../services/admissionScoreService';
import '../styles/pages/search-scores.css';

const SearchScoresPage = () => {
    const [filters, setFilters] = useState({
        examGroup: 'all',
        scoreRange: 'all',
        region: 'all',
        year: '2024',
    });
    const [scores, setScores] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchScores = async () => {
        try {
            setLoading(true);
            const data = await getAdmissionScores(filters);
            setScores(data);
            setError(null);
        } catch (err) {
            setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchScores();
    }, []);

    const handleFilterChange = (field, value) => {
        setFilters((prev) => ({
            ...prev,
            [field]: value,
        }));
    };

    const handleApplyFilters = () => {
        fetchScores();
    };

    return (
        <div className="ss-page">
            <section className="search-header">
                <h1>Tra cứu điểm chuẩn</h1>
                <p>Thông tin điểm chuẩn các trường qua các năm</p>
            </section>

            <div className="ss-content">
                <div className="ss-grid">
                    <aside className="filter-panel">
                        <h3>Bộ lọc tìm kiếm</h3>
                        <div className="filter-group">
                            <label>Khối thi</label>
                            <select
                                value={filters.examGroup}
                                onChange={(e) =>
                                    handleFilterChange(
                                        'examGroup',
                                        e.target.value,
                                    )
                                }
                            >
                                <option value="all">Tất cả khối</option>
                                <option value="A00">A00 (Toán, Lý, Hóa)</option>
                                <option value="A01">A01 (Toán, Lý, Anh)</option>
                                <option value="D01">
                                    D01 (Toán, Văn, Anh)
                                </option>
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Mức điểm</label>
                            <select
                                value={filters.scoreRange}
                                onChange={(e) =>
                                    handleFilterChange(
                                        'scoreRange',
                                        e.target.value,
                                    )
                                }
                            >
                                <option value="all">Tất cả</option>
                                <option value="above25">Trên 25 điểm</option>
                                <option value="20to25">20 - 25 điểm</option>
                                <option value="15to20">15 - 20 điểm</option>
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Khu vực</label>
                            <select
                                value={filters.region}
                                onChange={(e) =>
                                    handleFilterChange('region', e.target.value)
                                }
                            >
                                <option value="all">Toàn quốc</option>
                                <option value="north">Miền Bắc</option>
                                <option value="central">Miền Trung</option>
                                <option value="south">Miền Nam</option>
                            </select>
                        </div>
                        <div className="filter-actions">
                            <button
                                className="filter-apply-btn"
                                onClick={handleApplyFilters}
                            >
                                Áp dụng bộ lọc
                            </button>
                        </div>
                    </aside>

                    <div className="ss-scores-panel">
                        {loading ? (
                            <div className="loading-state">Đang tải dữ liệu...</div>
                        ) : error ? (
                            <div className="error-state">{error}</div>
                        ) : (
                            <>
                                <div className="ss-scores-header">
                                    <div className="year-filter">
                                        {[2024, 2023, 2022, 2021, 2020].map((year) => (
                                            <button
                                                key={year}
                                                className={
                                                    filters.year === year.toString()
                                                        ? 'active'
                                                        : ''
                                                }
                                                onClick={() =>
                                                    handleFilterChange(
                                                        'year',
                                                        year.toString(),
                                                    )
                                                }
                                            >
                                                {year}
                                            </button>
                                        ))}
                                    </div>
                                    <div className="total-results">
                                        Tổng số kết quả: <strong>{scores.length}</strong>
                                    </div>
                                </div>

                                <table className="ss-scores-table">
                                    <thead>
                                        <tr>
                                            <th>Trường</th>
                                            <th>Mã trường</th>
                                            <th>Ngành học</th>
                                            <th>Khối thi</th>
                                            <th>Điểm chuẩn</th>
                                            <th>Chỉ tiêu</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {scores.map((score, index) => (
                                            <tr key={score._id || index}>
                                                <td>
                                                    <Link
                                                        to={`/school/${score.universityCode}`}
                                                        className="school-link"
                                                    >
                                                        {score.universityName}
                                                    </Link>
                                                </td>
                                                <td>{score.universityCode}</td>
                                                <td>{score.majorName}</td>
                                                <td>{score.examGroup}</td>
                                                <td className="score-value">
                                                    {score.score}
                                                </td>
                                                <td>{score.quota}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SearchScoresPage;
