import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/pages/search-majors.css';

const SearchMajorsPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [filters, setFilters] = useState({
        field: 'all',
        examGroup: 'all',
        opportunity: 'all',
    });
    const [fields, setFields] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFields = async () => {
            try {
                const response = await fetch('/api/training-fields');
                const data = await response.json();
                setFields(data.data);
            } catch (error) {
                console.error('Error fetching fields:', error);
            } finally {
                setLoading(false);
            }
        };
        fetchFields();
    }, []);

    const handleSearch = (e) => {
        e.preventDefault();
        console.log('Searching for:', searchQuery);
    };

    const handleApplyFilters = () => {
        console.log('Applying filters:', filters);
    };

    return (
        <div className="sm-page">
            <section className="search-header">
                <h1>Tìm kiếm ngành học</h1>
                <p>Khám phá các ngành học và cơ hội nghề nghiệp tương lai</p>
                <form className="search-bar" onSubmit={handleSearch}>
                    <input
                        type="text"
                        placeholder="Nhập tên ngành học bạn quan tâm..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </form>
            </section>

            <div className="sm-content">
                <div className="sm-grid">
                    <div className="sm-filter-panel">
                        <h3>Bộ lọc tìm kiếm</h3>
                        <div className="filter-group">
                            <label>Khối ngành</label>
                            <select
                                value={filters.field}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        field: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Tất cả</option>
                                <option value="it">Công nghệ thông tin</option>
                                <option value="economics">Kinh tế</option>
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Khối thi</label>
                            <select
                                value={filters.examGroup}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        examGroup: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Tất cả</option>
                                <option value="a00">Khối A00</option>
                                <option value="b00">Khối B00</option>
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Cơ hội việc làm</label>
                            <select
                                value={filters.opportunity}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        opportunity: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Tất cả</option>
                                <option value="high">Cao</option>
                                <option value="medium">Trung bình</option>
                                <option value="low">Thấp</option>
                            </select>
                        </div>
                        <div className="filter-actions">
                            <button
                                className="filter-apply-btn"
                                onClick={handleApplyFilters}
                            >
                                Áp dụng bộ lọc
                            </button>
                        </div>
                    </div>

                    <div className="sm-results-panel">
                        {loading ? (
                            <div>Đang tải dữ liệu...</div>
                        ) : (
                            fields.map((field) => 
                                field.majors.map((major) => (
                                    <Link
                                        to={`/major/${major.major_id}`}
                                        key={major.major_id}
                                        className="sm-major-card"
                                    >
                                        <div className="sm-major-header">
                                            <h3>{major.major_name}</h3>
                                            <span className="sm-major-type">
                                                Mã ngành: {major.major_code}
                                            </span>
                                        </div>
                                        <div className="sm-major-content">
                                            <p>{major.description || 'Chưa có mô tả'}</p>
                                            <div className="sm-major-stats">
                                                <div className="sm-stat-item">
                                                    <div className="sm-stat-label">
                                                        Nhóm ngành
                                                    </div>
                                                    <div className="sm-stat-value">
                                                        {field.group_name}
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </Link>
                                ))
                            )
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SearchMajorsPage;
