import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/pages/school-detail.css';

const SchoolDetailPage = () => {
    const { id } = useParams();

    const schoolData = {
        hust: {
            name: "Đại học Bách Khoa Hà Nội",
            website: "https://hust.edu.vn",
            description: "Trường Đại học Bách khoa Hà Nội là trường đại học đa ngành về kỹ thuật...",
            address: "Số 1 Đại Cồ Việt, Hai Bà Trưng, Hà Nội"
        },
        uet: {
            name: "Đại học Công nghệ - ĐHQGHN",
            website: "https://uet.vnu.edu.vn",
            description: "Trường Đại học Công nghệ là trường thành viên của ĐHQGHN...",
            address: "144 Xuân Thủy, Cầu Giấy, Hà Nội"
        },
        // Thêm data cho các trường khác
    };

    const school = schoolData[id];

    return (
        <div className="school-detail-page">
            <section className="school-header">
                <h1>{school?.name}</h1>
            </section>

            <div className="school-content">
                <div className="school-info">
                    <h2>Thông tin chung</h2>
                    <p>{school?.description}</p>
                    <div className="school-meta">
                        <p>
                            <strong>Website chính thức: </strong>
                            <a href={school?.website} target="_blank" rel="noopener noreferrer">
                                {school?.website}
                            </a>
                        </p>
                        <p><strong>Địa chỉ: </strong>{school?.address}</p>
                    </div>
                </div>

                {/* Thêm các section khác về thông tin trường */}
            </div>
        </div>
    );
};

export default SchoolDetailPage;
