import axios from 'axios';

const API_URL = 'http://localhost:5000/api/major/detail';

export const majorDetailService = {
    getMajorDetailByCode: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}`);
            console.log('Raw API response:', response);

            // Kiểm tra response.data có tồn tại và có data không
            if (response.data && response.data.data) {
                const majorData = response.data.data;
                return {
                    success: true,
                    data: {
                        major_code: majorData.maNganh || code,
                        major_name: majorData.tenNganh || '',
                        description: majorData.moTa || '',
                        job_opportunities: majorData.coHoiViecLam || '',
                        salary_range: majorData.mucLuong || '',
                        exam_groups: Array.isArray(majorData.khoiThi) ? majorData.khoiThi : [],
                        duration: majorData.thoiGianDaoTao || '',
                        skills: Array.isArray(majorData.kyNang) ? majorData.kyNang : [],
                        subjects: Array.isArray(majorData.monHocChinh) ? majorData.monHocChinh : []
                    }
                };
            }
            return { success: false, error: 'Không tìm thấy thông tin ngành' };
        } catch (error) {
            console.error('Error fetching major details:', error);
            return {
                success: false,
                error: 'Có lỗi xảy ra khi tải thông tin ngành',
            };
        }
    },

    getMajorStatistics: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}/statistics`);
            return response.data;
        } catch (error) {
            return { success: false, error: error.message };
        }
    },

    getMajorSchools: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}/schools`);
            return response.data;
        } catch (error) {
            return { success: false, error: error.message };
        }
    },
};
