import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { majorDetailService } from '../../services/majorDetailService';
import '../../styles/pages/major-detail.css';

const MajorDetailPage = () => {
    const { majorCode } = useParams();
    const [majorData, setMajorData] = useState(null);

    useEffect(() => {
        const fetchMajorDetail = async () => {
            if (majorCode) {
                const result = await majorDetailService.getMajorDetailByCode(majorCode);
                if (result.success && result.data) {
                    setMajorData(result.data);
                }
            }
        };
        fetchMajorDetail();
    }, [majorCode]);

    if (!majorData) return <div>Đang tải thông tin...</div>;

    return (
        <div className="major-detail-page">
            <div className="major-detail-header">
                <Link to="/search/majors" className="back-link">
                    <i className="fas fa-arrow-left"></i> Quay lại
                </Link>
                <h1>{majorData.major_name}</h1>
                <div className="major-code">Mã ngành: {majorData.major_code}</div>
            </div>

            <div className="major-detail-content">
                <section className="major-section">
                    <h2>Mô tả ngành học</h2>
                    <div className="section-content">
                        {majorData.description}
                    </div>
                </section>

                <section className="major-section">
                    <h2>Cơ hội việc làm</h2>
                    <div className="section-content">
                        {majorData.job_opportunities}
                    </div>
                </section>

                <section className="major-section">
                    <h2>Mức lương tham khảo</h2>
                    <div className="section-content">
                        {majorData.salary_range}
                    </div>
                </section>
                {majorData.exam_groups && majorData.exam_groups.length > 0 && (
                    <section className="major-section">
                        <h2>Khối thi</h2>
                        <div className="section-content exam-groups">
                            {majorData.exam_groups.map((group, idx) => (
                                <span key={idx} className="exam-group-tag">
                                    {group}
                                </span>
                            ))}
                        </div>
                    </section>
                )}
            </div>
        </div>
    );
};

export default MajorDetailPage;
