import axios from 'axios';

const API_URL = 'http://localhost:5000/api/major/detail';

export const majorDetailService = {
    getMajorDetailByCode: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}`);
            console.log('API Response:', response.data); // Debug log

            // Lấy trực tiếp data từ response
            const majorData = response.data;

            if (majorData) {
                return {
                    success: true,
                    data: {
                        major_code: majorData.major_code,
                        major_name: majorData.name, // thay đổi tên trường
                        description: majorData.description,
                        job_opportunities: majorData.career_opportunities, // thay đổi tên trường
                        salary_range: majorData.salary_range
                    }
                };
            }
            return { success: false, error: 'Không tìm thấy thông tin ngành' };
        } catch (error) {
            console.error('Error:', error);
            return {
                success: false,
                error: 'Có lỗi xảy ra khi tải thông tin ngành',
            };
        }
    },

    getMajorStatistics: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}/statistics`);
            return response.data;
        } catch (error) {
            return { success: false, error: error.message };
        }
    },

    getMajorSchools: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}/schools`);
            return response.data;
        } catch (error) {
            return { success: false, error: error.message };
        }
    },
};
