import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { majorDetailService } from '../../services/majorDetailService';
import '../../styles/pages/major-detail.css';

// Component hiển thị header
const MajorHeader = ({ majorName, majorCode }) => (
    <div className="major-detail-header">
        <Link to="/search/majors" className="back-link">
            <i className="fas fa-arrow-left"></i> Quay lại
        </Link>
        <h1>{majorName}</h1>
        <div className="major-code">Mã ngành: {majorCode}</div>
    </div>
);

// Component hiển thị section thông tin
const InfoSection = ({ title, content }) => (
    <section className="major-section">
        <h2>{title}</h2>
        <div className="section-content">
            {content || 'Chưa có thông tin'}
        </div>
    </section>
);

// Component hiển thị khối thi
const ExamGroups = ({ groups }) => (
    <section className="major-section">
        <h2>Khối thi</h2>
        <div className="section-content exam-groups">
            {groups && groups.length > 0 ? (
                groups.map((group) => (
                    <span key={group} className="exam-group-tag">
                        {group}
                    </span>
                ))
            ) : (
                'Chưa có thông tin'
            )}
        </div>
    </section>
);

const MajorDetailPage = () => {
    const { majorCode } = useParams();
    const [majorData, setMajorData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMajorDetail = async () => {
            try {
                setLoading(true);
                const result = await majorDetailService.getMajorDetailByCode(majorCode);
                if (result.success) {
                    setMajorData(result.data);
                } else {
                    setError(result.error);
                }
            } catch (err) {
                setError('Có lỗi xảy ra khi tải thông tin ngành học');
            } finally {
                setLoading(false);
            }
        };

        fetchMajorDetail();
    }, [majorCode]);

    if (loading) return <div className="loading">Đang tải thông tin ngành...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!majorData) return <div className="not-found">Không tìm thấy thông tin ngành</div>;

    return (
        <div className="major-detail-page">
            <MajorHeader 
                majorName={majorData.major_name} 
                majorCode={majorData.major_code} 
            />

            <div className="major-detail-content">
                <InfoSection 
                    title="Mô tả ngành học" 
                    content={majorData.description} 
                />

                <InfoSection 
                    title="Cơ hội việc làm" 
                    content={majorData.job_opportunities} 
                />

                <InfoSection 
                    title="Mức lương tham khảo" 
                    content={majorData.salary_range} 
                />

                <ExamGroups groups={majorData.exam_groups} />
            </div>
        </div>
    );
};

export default MajorDetailPage;
