import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../../styles/pages/search-schools.css';
import { schoolService } from '../../services/schoolService';

const SearchSchoolsPage = () => {
    const [filters, setFilters] = useState({
        type: 'all',
        region: 'all',
        tuition: 'all',
    });
    const [searchQuery, setSearchQuery] = useState('');
    const [schools, setSchools] = useState([]);
    const [filteredSchools, setFilteredSchools] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    const isSchoolCode = (query) => {
        const schoolCodePattern = /^[A-Z0-9]{1,3}$/;
        return schoolCodePattern.test(query.toUpperCase());
    };

    useEffect(() => {
        const fetchSchools = async () => {
            setLoading(true);
            const result = await schoolService.getAll();
            console.log('Kết quả trả về:', result);
            if (result.success) {
                setSchools(result.data);
            } else {
                setError(result.error);
            }
            setLoading(false);
        };
        fetchSchools();
    }, []);

    useEffect(() => {
        setFilteredSchools(schools);
    }, [schools]);

    const handleSearch = (e) => {
        e.preventDefault();
        setCurrentPage(1);
        setError(null); // Reset error state

        try {
            if (!schools || !Array.isArray(schools)) {
                throw new Error('Dữ liệu trường không hợp lệ');
            }

            let filtered = [...schools];
            const query = searchQuery.trim().toLowerCase();

            // Validate inputs before filtering
            if (!filters || typeof filters !== 'object') {
                throw new Error('Bộ lọc không hợp lệ');
            }

            if (query) {
                filtered = filtered.filter((school) => {
                    try {
                        if (isSchoolCode(query)) {
                            return school.code?.toString().toLowerCase() === query;
                        }
                        return (
                            (school.name && school.name.toLowerCase().includes(query)) ||
                            (school.location && school.location.toLowerCase().includes(query)) ||
                            (school.code && school.code.toString().toLowerCase().includes(query)) ||
                            (school.majors && Array.isArray(school.majors) && 
                             school.majors.some(major => major.name && major.name.toLowerCase().includes(query)))
                        );
                    } catch (err) {
                        console.error('Lỗi khi lọc trường:', err);
                        return false;
                    }
                });
            }

            // Apply filters with validation
            filtered = applyFiltersWithValidation(filtered, filters);

            setFilteredSchools(filtered);
        } catch (err) {
            console.error('Search error:', err);
            setError(err.message || 'Có lỗi xảy ra khi tìm kiếm. Vui lòng thử lại');
            setFilteredSchools([]); // Clear results on error
        }
    };

    // Helper function to apply filters with validation
    const applyFiltersWithValidation = (schools, filters) => {
        try {
            let results = [...schools];

            if (filters.type !== 'all') {
                results = results.filter(school => 
                    school && school.type === filters.type
                );
            }

            if (filters.region !== 'all') {
                const regionKeywords = {
                    'north': ['bắc', 'hà nội', 'hải phòng'],
                    'central': ['trung', 'huế', 'đà nẵng'],
                    'south': ['nam', 'hồ chí minh', 'cần thơ']
                };

                results = results.filter(school => {
                    if (!school || !school.location) return false;
                    const location = school.location.toLowerCase();
                    return regionKeywords[filters.region].some(keyword => 
                        location.includes(keyword)
                    );
                });
            }

            return results;
        } catch (err) {
            console.error('Filter error:', err);
            throw new Error('Lỗi khi áp dụng bộ lọc');
        }
    };

    const handleSearchInputChange = (e) => {
        setSearchQuery(e.target.value);
        if (!e.target.value.trim()) {
            setFilteredSchools(schools); // Reset to all schools if search is cleared
        }
    };

    const paginateSchools = (filtered) => {
        const indexOfLastSchool = currentPage * itemsPerPage;
        const indexOfFirstSchool = indexOfLastSchool - itemsPerPage;
        return filtered.slice(indexOfFirstSchool, indexOfLastSchool);
    };

    const handlePagination = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const totalPages = Math.ceil(filteredSchools.length / itemsPerPage);

    return (
        <div className="ssh-page">
            <section className="search-header">
                <h1>Khám phá trường đại học</h1>
                <p>
                    Tìm hiểu và so sánh các trường đại học, cao đẳng trên toàn
                    quốc
                </p>
                <form className="search-box" onSubmit={handleSearch}>
                    <input
                        type="text"
                        placeholder="Tìm kiếm theo tên trường, mã trường..."
                        value={searchQuery}
                        onChange={handleSearchInputChange}
                    />
                    <button type="submit">
                        <i className="fas fa-search"></i>
                        Tìm kiếm
                    </button>
                </form>
                <div className="search-stats">
                    <span>
                        <strong>300+</strong> trường
                    </span>
                    <span>
                        <strong>400+</strong> ngành học
                    </span>
                    <span>
                        <strong>1,200+</strong> đánh giá
                    </span>
                </div>
            </section>

            <div className="ssh-content">
                <div className="ssh-grid">
                    <aside className="filter-panel">
                        <h3>Bộ lọc tìm kiếm</h3>
                        <div className="ssh-filter-group">
                            <label>Loại trường</label>
                            <select
                                value={filters.type}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        type: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Tất cả</option>
                                <option value="public">Đại học công lập</option>
                                <option value="private">Đại học dân lập</option>
                                <option value="college">Cao đẳng</option>
                            </select>
                        </div>
                        <div className="ssh-filter-group">
                            <label>Khu vực</label>
                            <select
                                value={filters.region}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        region: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Toàn quốc</option>
                                <option value="north">Miền Bắc</option>
                                <option value="central">Miền Trung</option>
                                <option value="south">Miền Nam</option>
                            </select>
                        </div>
                        <div className="ssh-filter-group">
                            <label>Học phí</label>
                            <select
                                value={filters.tuition}
                                onChange={(e) =>
                                    setFilters({
                                        ...filters,
                                        tuition: e.target.value,
                                    })
                                }
                            >
                                <option value="all">Tất cả mức</option>
                                <option value="low">Dưới 15 triệu/năm</option>
                                <option value="medium">15-25 triệu/năm</option>
                                <option value="high">Trên 25 triệu/năm</option>
                            </select>
                        </div>
                        <div className="ssh-filter-actions">
                            <button
                                className="ssh-filter-apply-btn"
                                onClick={handleSearch}
                            >
                                Áp dụng bộ lọc
                            </button>
                            <button
                                className="filter-reset-btn"
                                onClick={() => {
                                    setFilters({
                                        type: 'all',
                                        region: 'all',
                                        tuition: 'all',
                                    });
                                    setSearchQuery('');
                                    setFilteredSchools(schools);
                                }}
                            >
                                Đặt lại
                            </button>
                        </div>
                    </aside>

                    <div className="ssh-results-panel">
                        {error && (
                            <div className="ssh-error-message">
                                <i className="fas fa-exclamation-circle"></i>
                                {error}
                            </div>
                        )}
                        <div className="ssh-results-header">
                            <div className="ssh-found-count">
                                Tìm thấy{' '}
                                <strong>{filteredSchools.length}</strong> trường
                            </div>
                            <div className="ssh-sort-options">
                                <label>Sắp xếp:</label>
                                <select>
                                    <option>Phù hợp nhất</option>
                                    <option>A-Z</option>
                                    <option>Điểm chuẩn cao nhất</option>
                                </select>
                            </div>
                        </div>

                        <div className="ssh-schools-grid">
                            {loading ? (
                                <div>Loading...</div>
                            ) : error ? (
                                <div className="error-message">{error}</div>
                            ) : (
                                paginateSchools(filteredSchools).map(
                                    (school) => (
                                        <Link
                                            to={`/school/${school.id}`}
                                            key={school.id}
                                            className="ssh-school-card"
                                        >
                                            <div className="ssh-school-image">
                                                <img
                                                    src={`/images/school-${school.id}.jpg`}
                                                    alt="School"
                                                />
                                                <div className="ssh-school-type">
                                                    {school.ownership}
                                                </div>
                                            </div>
                                            <div className="ssh-school-content">
                                                <div className="ssh-school-main">
                                                    <div className="ssh-school-header">
                                                        <h3 className="ssh-school-name">
                                                            {school.school_name}
                                                        </h3>
                                                        <span className="ssh-school-code">
                                                            {school.school_id}
                                                        </span>
                                                    </div>
                                                    <div className="ssh-school-info">
                                                        <div className="ssh-info-item">
                                                            <i className="fas fa-map-marker-alt"></i>
                                                            <span>
                                                                {
                                                                    school.location
                                                                }
                                                            </span>
                                                        </div>
                                                        <div className="ssh-info-item">
                                                            <i className="fas fa-graduation-cap"></i>
                                                            <span>
                                                                12,000 sinh viên
                                                            </span>
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="ssh-school-details">
                                                    <div className="ssh-stat-item">
                                                        <div className="ssh-stat-label">
                                                            Điểm chuẩn
                                                        </div>
                                                        <div className="ssh-stat-value">
                                                            23.5-27.5
                                                        </div>
                                                        <div className="ssh-stat-range">
                                                            Năm 2023
                                                        </div>
                                                    </div>
                                                    <div className="ssh-stat-item">
                                                        <div className="ssh-stat-label">
                                                            Học phí/năm
                                                        </div>
                                                        <div className="ssh-stat-value">
                                                            15-20tr
                                                        </div>
                                                    </div>
                                                    <div className="ssh-stat-item">
                                                        <div className="ssh-stat-label">
                                                            Tỷ lệ có việc làm
                                                        </div>
                                                        <div className="ssh-stat-value">
                                                            92%
                                                        </div>
                                                        <div className="ssh-stat-note">
                                                            Sau 12 tháng
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </Link>
                                    ),
                                )
                            )}
                        </div>

                        <div className="ssh-pagination">
                            <button
                                onClick={() =>
                                    handlePagination(currentPage - 1)
                                }
                                disabled={currentPage === 1}
                                className="pagination-btn"
                            >
                                Trước
                            </button>
                            {[...Array(totalPages)].map((_, index) => {
                                const pageNumber = index + 1;
                                if (
                                    pageNumber === 1 ||
                                    pageNumber === totalPages ||
                                    (pageNumber >= currentPage - 2 &&
                                        pageNumber <= currentPage + 2)
                                ) {
                                    return (
                                        <button
                                            key={pageNumber}
                                            className={`pagination-btn ${currentPage === pageNumber ? 'active' : ''}`}
                                            onClick={() =>
                                                handlePagination(pageNumber)
                                            }
                                        >
                                            {pageNumber}
                                        </button>
                                    );
                                }
                                if (
                                    pageNumber === currentPage - 3 ||
                                    pageNumber === currentPage + 3
                                ) {
                                    return (
                                        <span
                                            key={pageNumber}
                                            className="pagination-ellipsis"
                                        >
                                            ...
                                        </span>
                                    );
                                }
                                return null;
                            })}
                            <button
                                onClick={() =>
                                    handlePagination(currentPage + 1)
                                }
                                disabled={currentPage === totalPages}
                                className="pagination-btn"
                            >
                                Sau
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SearchSchoolsPage;
