import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/pages/search-majors.css';
import { majorService } from '../services/majorService';

const ITEMS_PER_PAGE = 6;

const defaultMajors = [
  {
    major_code: "7480201",
    major_name: "Công nghệ thông tin",
    group_id: "tech",
    exam_groups: ["A00", "A01"],
    job_opportunity: "high"
  },
  {
    major_code: "7340101", 
    major_name: "Quản trị kinh doanh",
    group_id: "business",
    exam_groups: ["A00", "D01"],
    job_opportunity: "high"
  },
  {
    major_code: "7720101",
    major_name: "Y khoa",
    group_id: "medical",
    exam_groups: ["B00"],
    job_opportunity: "high"
  },
  {
    major_code: "7380101",
    major_name: "Luật",
    group_id: "social",
    exam_groups: ["D01"],
    job_opportunity: "medium"
  },
  {
    major_code: "7510201",
    major_name: "Công nghệ kỹ thuật cơ khí",
    group_id: "engineering",
    exam_groups: ["A00", "A01"],
    job_opportunity: "medium"
  },
  {
    major_code: "7220201",
    major_name: "Ngôn ngữ Anh",
    group_id: "language",
    exam_groups: ["D01"],
    job_opportunity: "medium"
  }
];

const SearchMajorsPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [majors, setMajors] = useState([]);
    const [originalMajors, setOriginalMajors] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);

    useEffect(() => {
        const fetchMajors = async () => {
            try {
                setLoading(true);
                // Temporarily use default data instead of API call
                setMajors(defaultMajors);
                setOriginalMajors(defaultMajors);
            } catch (error) {
                console.error('Error:', error);
                setMajors([]);
            } finally {
                setLoading(false);
            }
        };
        fetchMajors();
    }, []);

    // Calculate current items to display
    const getCurrentItems = () => {
        const indexOfLastItem = currentPage * ITEMS_PER_PAGE;
        const indexOfFirstItem = indexOfLastItem - ITEMS_PER_PAGE;
        return majors.slice(indexOfFirstItem, indexOfLastItem);
    };

    const handleSearch = (e) => {
        e.preventDefault();
        if (!searchQuery) {
            setMajors(originalMajors);
            return;
        }

        const results = originalMajors.filter(major => 
            major.major_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            major.major_code.toLowerCase().includes(searchQuery.toLowerCase())
        );

        setMajors(results);
    };

    return (
        <div className="sm-page">
            <section className="search-header">
                <h1>Tìm kiếm ngành học</h1>
                <p>Khám phá các ngành học và cơ hội nghề nghiệp tương lai</p>
                <form className="search-bar" onSubmit={handleSearch}>
                    <input
                        type="text"
                        placeholder="Nhập tên ngành học bạn quan tâm..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </form>
            </section>

            <div className="sm-content">
                <div className="sm-grid">
                    <div className="sm-filter-panel">
                        <h3>Bộ lọc tìm kiếm</h3>
                        <div className="filter-group">
                            <label>Khối ngành</label>
                            <select
                                // value={filters.field}
                                // onChange={(e) =>
                                //     setFilters({
                                //         ...filters,
                                //         field: e.target.value,
                                //     })
                                // }
                            >
                                <option value="all">Tất cả</option>
                                {/* {filterOptions.map(option => (
                                    <option key={option.value} value={option.value}>
                                        {option.label}
                                    </option>
                                ))} */}
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Khối thi</label>
                            <select
                                // value={filters.examGroup}
                                // onChange={(e) =>
                                //     setFilters({
                                //         ...filters,
                                //         examGroup: e.target.value,
                                //     })
                                // }
                            >
                                <option value="all">Tất cả</option>
                                <option value="a00">Khối A00</option>
                                <option value="b00">Khối B00</option>
                            </select>
                        </div>
                        <div className="filter-group">
                            <label>Cơ hội việc làm</label>
                            <select
                                // value={filters.opportunity}
                                // onChange={(e) =>
                                //     setFilters({
                                //         ...filters,
                                //         opportunity: e.target.value,
                                //     })
                                // }
                            >
                                <option value="all">Tất cả</option>
                                <option value="high">Cao</option>
                                <option value="medium">Trung bình</option>
                                <option value="low">Thấp</option>
                            </select>
                        </div>
                        <div className="filter-actions">
                            <button
                                className="filter-apply-btn"
                                // onClick={handleApplyFilters}
                            >
                                Áp dụng bộ lọc
                            </button>
                        </div>
                    </div>

                    <div className="sm-results-panel">
                        {loading ? (
                            <div>Đang tải dữ liệu...</div>
                        ) : majors.length === 0 ? (
                            <div>Không có dữ liệu</div>
                        ) : (
                            <div className="majors-grid">
                                {getCurrentItems().map((major) => (
                                    <Link 
                                        to={`/major/${major.major_code}`}
                                        key={major.major_code} 
                                        className="sm-major-card"
                                    >
                                        <div className="sm-major-header">
                                            <h4>{major.major_name}</h4>
                                            <span className="major-code">
                                                Mã ngành: {major.major_code}
                                            </span>
                                        </div>
                                    </Link>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SearchMajorsPage;
