import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { schoolDetailService } from '../../services/schoolDetailService';
import '../../styles/pages/school-detail.css';

const SchoolDetailPage = () => {
    const { code } = useParams();
    const [school, setSchool] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('overview');

    useEffect(() => {
        const fetchSchoolDetails = async () => {
            try {
                setLoading(true);
                const response = await schoolDetailService.getSchoolByCode(code);
                if (response.success) {
                    setSchool(response.data);
                } else {
                    setError(response.error);
                }
            } catch (err) {
                setError('Không thể tải thông tin trường');
            } finally {
                setLoading(false);
            }
        };

        if (code) {
            fetchSchoolDetails();
        }
    }, [code]);

    if (loading) return <div>Đang tải thông tin...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!school) return <div>Không tìm thấy thông tin trường</div>;

    return (
        <div className="sd-school-detail-page">
            <section className="sd-school-banner">
                <div className="sd-banner-content">
                    <div className="sd-school-header">
                        <div className="sd-school-logo">
                            <img
                                src={school.school_img || "/placeholder-school.jpg"}
                                alt={`Logo ${school.school_name}`}
                                onError={(e) => e.target.src = "/placeholder-school.jpg"}
                            />
                        </div>
                        <div className="sd-school-info">
                            <h1>{school.school_name}</h1>
                            <div className="sd-school-meta">
                                <p className="sd-school-code">Mã trường: {school.code}</p>
                                {school.location && (
                                    <p className="sd-school-location">
                                        <i className="fas fa-map-marker-alt"></i>
                                        {school.location}
                                    </p>
                                )}
                                {school.website && (
                                    <p className="sd-school-website">
                                        <i className="fas fa-globe"></i>
                                        <a href={school.website} target="_blank" rel="noopener noreferrer">
                                            {school.website}
                                        </a>
                                    </p>
                                )}
                                {school.phones && school.phones.length > 0 && (
                                    <p className="sd-school-phone">
                                        <i className="fas fa-phone"></i>
                                        {school.phones.join(" - ")}
                                    </p>
                                )}
                            </div>
                        </div>
                    </div>

                    <div className="sd-school-stats">
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Thành lập</span>
                            <span className="sd-stat-value">{school.founded_year || 'N/A'}</span>
                        </div>
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Loại trường</span>
                            <span className="sd-stat-value">{school.ownership || 'N/A'}</span>
                        </div>
                        {school.ranking && (
                            <div className="sd-stat-item">
                                <span className="sd-stat-label">Xếp hạng</span>
                                <span className="sd-stat-value">#{school.ranking}</span>
                            </div>
                        )}
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Quy mô</span>
                            <span className="sd-stat-value">
                                {school.student_count?.toLocaleString() || 'N/A'}
                            </span>
                        </div>
                    </div>
                </div>
            </section>

            <nav className="sd-school-nav">
                <div className="sd-nav-container">
                    <ul>
                        {['overview', 'majors', 'scores', 'facilities'].map(tab => (
                            <li key={tab}>
                                <button 
                                    className={activeTab === tab ? 'active' : ''}
                                    onClick={() => setActiveTab(tab)}
                                >
                                    {getTabLabel(tab)}
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </nav>

            <main className="sd-main-content">
                <div className="sd-content-container">
                    <section className="sd-overview">
                        <h2>Giới thiệu chung</h2>
                        <div className="sd-description">
                            {school.description || 'Chưa có thông tin mô tả'}
                        </div>

                        {school.method_link && (
                            <div className="sd-admission-method">
                                <h3>Phương thức tuyển sinh</h3>
                                <a href={school.method_link} 
                                   target="_blank" 
                                   rel="noopener noreferrer"
                                   className="sd-method-link">
                                    Xem đề án tuyển sinh
                                </a>
                            </div>
                        )}
                    </section>

                    {renderContent(activeTab, school)}
                </div>
            </main>
        </div>
    );
};

const getTabLabel = (tab) => {
    const labels = {
        overview: 'Tổng quan',
        majors: 'Ngành đào tạo',
        scores: 'Điểm chuẩn',
        facilities: 'Cơ sở vật chất'
    };
    return labels[tab];
};

const renderContent = (tab, school) => {
    switch (tab) {
        case 'overview':
            return (
                <section className="sd-content-section">
                    <h2 className="sd-section-title">Tổng quan về trường</h2>
                    <div className="sd-overview-description">
                        <p>{school.description}</p>
                        {school.website && (
                            <a href={school.website} target="_blank" rel="noopener noreferrer" 
                               className="sd-website-button">
                                <span>Truy cập website chính thức</span>
                                <i className="fas fa-external-link-alt"></i>
                            </a>
                        )}
                    </div>
                </section>
            );

        case 'majors':
            return (
                <section className="sd-content-section">
                    <h2 className="sd-section-title">Ngành đào tạo</h2>
                    <div className="sd-majors-grid">
                        {school.majors?.map(major => (
                            <div key={major.code} className="sd-major-card">
                                <h3>{major.name}</h3>
                                <p>Mã ngành: {major.code}</p>
                                {major.exam_groups && (
                                    <p>Khối thi: {major.exam_groups.join(', ')}</p>
                                )}
                            </div>
                        ))}
                    </div>
                </section>
            );

        // ... add other cases for scores and facilities ...

        default:
            return null;
    }
};

export default SchoolDetailPage;
