import axios from 'axios';

const API_URL = 'http://localhost:5000/api/major/detail';

export const majorDetailService = {
    getAllMajorDetails: async () => {
        const response = await axios.get(API_URL);
        return response.data;
    },
    createMajorDetail: async (majorDetail) => {
        const response = await axios.post(API_URL, majorDetail);
        return response.data;
    },
    updateMajorDetail: async (id, majorDetail) => {
        const response = await axios.put(`${API_URL}/${id}`, majorDetail);
        return response.data;
    },
    deleteMajorDetail: async (id) => {
        const response = await axios.delete(`${API_URL}/${id}`);
        return response.data;
    },
    getMajorDetailByCode: async (code) => {
        try {
            const response = await axios.get(`${API_URL}/${code}`);
            if (response.data) {
                // Map dữ liệu từ API response sang format hiển thị
                return {
                    name: response.data.major_name,
                    code: response.data.major_code,
                    description: response.data.description || 'Chưa cập nhật',
                    jobOpportunities: response.data.job_opportunities || 'Chưa cập nhật',
                    salaryRange: response.data.salary_range || 'Chưa cập nhật',
                    // Thêm các trường mặc định cho các thông tin chưa có trong DB
                    examBlocks: 'Đang cập nhật',
                    benchmarkScore: 'Đang cập nhật', 
                    tuitionFee: 'Đang cập nhật',
                    duration: '4 năm',
                    degree: 'Cử nhân',
                    employmentRate: 'Đang cập nhật',
                    demandLevel: 'Đang cập nhật',
                    startingSalary: 'Đang cập nhật',
                    studyAbroadOpportunity: 'Có',
                    curriculum: [],
                    careerProspects: [],
                    universities: []
                };
            }
            return null;
        } catch (error) {
            console.error('Error fetching major details:', error);
            return null;
        }
    }
};
