import axios from 'axios';

const API_URL = 'http://localhost:5000/api/search/schools';

export const schoolService = {
    getAll: async () => {
        try {
            const res = await axios.get(API_URL);
            console.log('API Response:', res.data);

            // Process response data
            if (Array.isArray(res.data)) {
                return {
                    success: true,
                    data: res.data.map((school) => ({
                        id: school.school_id,
                        name: school.school_name,
                        location: school.location,
                        type: school.school_type,
                        ownership: school.ownership,
                        school_img: school.school_img
                    })),
                };
            }

            // Handle wrapped response format
            const { success, data } = res.data;
            if (success && Array.isArray(data)) {
                return {
                    success: true,
                    data: data.map((school) => ({
                        id: school.school_id,
                        name: school.school_name,
                        location: school.location,
                        type: school.school_type,
                        ownership: school.ownership,
                        school_img: school.school_img
                    })),
                };
            }

            return {
                success: false,
                error: 'Không nhận được dữ liệu hoặc định dạng không hợp lệ'
            };
        } catch (error) {
            console.error('Lỗi khi tải danh sách trường:', error);
            return { 
                success: false, 
                error: error.message || 'Có lỗi xảy ra khi tải dữ liệu'
            };
        }
    },

    searchSchools: async (query) => {
        try {
            const response = await axios.get(
                `${API_URL}/search?q=${encodeURIComponent(query)}`,
            );
            const { success, data } = response.data;
            if (success) {
                return { success: true, data };
            }
            return { success: false, error: 'Search failed' };
        } catch (error) {
            return { success: false, error: error.message };
        }
    },
};
