import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { schoolDetailService } from '../../services/schoolDetailService';
import '../../styles/pages/school-detail.css';

const SchoolDetailPage = () => {
    const { code } = useParams();
    const [school, setSchool] = useState(null);
    const [majors, setMajors] = useState([]);
    const [scores, setScores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState('overview');

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const [schoolRes, majorsRes, scoresRes] = await Promise.all([
                    schoolDetailService.getSchoolByCode(code),
                    schoolDetailService.getMajorsBySchool(code),
                    schoolDetailService.getSchoolScores(code)
                ]);

                if (schoolRes.success) setSchool(schoolRes.data);
                if (majorsRes.success) setMajors(majorsRes.data);
                if (scoresRes.success) setScores(scoresRes.data);
            } catch (err) {
                setError('Không thể tải thông tin trường');
            } finally {
                setLoading(false);
            }
        };

        if (code) {
            fetchData();
        }
    }, [code]);

    if (loading) return <div>Đang tải thông tin...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!school) return <div>Không tìm thấy thông tin trường</div>;

    return (
        <div className="sd-school-detail-page">
            <section className="sd-school-banner">
                <div className="sd-banner-content">
                    <div className="sd-school-header">
                        <div className="sd-school-logo">
                            <img
                                src={school.school_img || "/placeholder-school.jpg"}
                                alt={`Logo ${school.school_name}`}
                                onError={(e) => e.target.src = "/placeholder-school.jpg"}
                            />
                        </div>
                        <div className="sd-school-info">
                            <h1>{school.school_name}</h1>
                            <div className="sd-school-meta">
                                <p className="sd-school-code">Mã trường: {school.code}</p>
                            </div>
                        </div>
                    </div>

                    <div className="sd-school-stats">
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Thành lập</span>
                            <span className="sd-stat-value">{school.founded_year || 'N/A'}</span>
                        </div>
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Loại trường</span>
                            <span className="sd-stat-value">{school.ownership || 'N/A'}</span>
                        </div>
                        {school.ranking && (
                            <div className="sd-stat-item">
                                <span className="sd-stat-label">Xếp hạng</span>
                                <span className="sd-stat-value">#{school.ranking}</span>
                            </div>
                        )}
                        <div className="sd-stat-item">
                            <span className="sd-stat-label">Quy mô</span>
                            <span className="sd-stat-value">
                                {school.student_count?.toLocaleString() || 'N/A'}
                            </span>
                        </div>
                    </div>
                </div>
            </section>

            <nav className="sd-school-nav">
                <div className="sd-nav-container">
                    <ul>
                        {['overview', 'majors', 'scores', 'facilities'].map(tab => (
                            <li key={tab}>
                                <button 
                                    className={activeTab === tab ? 'active' : ''}
                                    onClick={() => setActiveTab(tab)}
                                >
                                    {getTabLabel(tab)}
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            </nav>

            <main className="sd-main-content">
                <div className="sd-content-container">
                    {renderContent(activeTab, school, majors, scores)}
                </div>
            </main>
        </div>
    );
};

const getTabLabel = (tab) => {
    const labels = {
        overview: 'Tổng quan',
        majors: 'Ngành đào tạo',
        scores: 'Điểm chuẩn',
        facilities: 'Cơ sở vật chất'
    };
    return labels[tab];
};

const renderContent = (tab, school, majors, scores) => {
    switch (tab) {
        case 'overview':
            return (
                <section className="sd-content-section">
                    <h2 className="sd-section-title">Tổng quan về trường</h2>
                    <div className="sd-overview-description">
                        <p>{school?.description}</p>

                        <div className="sd-contact-info">
                            <h3>Thông tin liên hệ</h3>
                            <div className="sd-contact-list">
                                {school?.location && (
                                    <div className="sd-contact-item">
                                        <i className="fas fa-map-marker-alt"></i>
                                        <span>{school.location}</span>
                                    </div>
                                )}
                                {school?.website && (
                                    <div className="sd-contact-item">
                                        <i className="fas fa-globe"></i>
                                        <a href={school.website} target="_blank" rel="noopener noreferrer">
                                            {school.website}
                                        </a>
                                    </div>
                                )}
                                {school?.phones && school.phones.length > 0 && (
                                    <div className="sd-contact-item">
                                        <i className="fas fa-phone"></i>
                                        <span>{school.phones.join(" - ")}</span>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </section>
            );

        case 'majors':
            return (
                <section className="sd-content-section">
                    <h2 className="sd-section-title">
                        Ngành đào tạo
                        {school.method_link && (
                            <a 
                                href={school.method_link}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="sd-quota-button"
                            >
                                <i className="fas fa-file-alt"></i>
                                Xem chỉ tiêu tuyển sinh
                            </a>
                        )}
                    </h2>

                    <div className="sd-majors-grid">
                        {majors && majors.length > 0 ? (
                            majors.map((major) => (
                                <div key={major.majorCode} className="sd-major-card">
                                    <div className="sd-major-header">
                                        <h3>{major.majorName}</h3>
                                        <span className="sd-major-code">{major.majorCode}</span>
                                    </div>

                                    <div className="sd-major-details">
                                        <div className="sd-major-detail-item">
                                            <label>Tổ hợp môn xét tuyển:</label>
                                            <p>{major.subjects?.join(", ") || "Chưa cập nhật"}</p>
                                        </div>

                                        {major.quota && (
                                            <div className="sd-major-detail-item">
                                                <label>Chỉ tiêu 2024:</label>
                                                <p>{major.quota} sinh viên</p>
                                            </div>
                                        )}

                                        {major.lastYearScore && (
                                            <div className="sd-major-detail-item">
                                                <label>Điểm chuẩn 2023:</label>
                                                <p>{major.lastYearScore} điểm</p>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            ))
                        ) : (
                            <div className="sd-no-data">
                                Chưa có thông tin ngành đào tạo
                            </div>
                        )}
                    </div>
                </section>
            );

        case 'scores':
            return (
                <section className="sd-content-section">
                    <h2 className="sd-section-title">Điểm chuẩn</h2>
                    {scores && scores.length > 0 ? (
                        <div className="sd-scores-table">
                            <table>
                                <thead>
                                    <tr>
                                        <th>Tên ngành</th>
                                        <th>Mã ngành</th>
                                        <th>Điểm chuẩn</th>
                                        <th>Tổ hợp môn</th>
                                        <th>Năm</th>
                                        <th>Ghi chú</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {scores.map((score, index) => (
                                        <tr key={`${score.majorCode}-${score.year}-${index}`}>
                                            <td>{score.majorName}</td>
                                            <td>{score.majorCode}</td>
                                            <td>{score.score}</td>
                                            <td>{score.subjects?.join(", ") || "N/A"}</td>
                                            <td>{score.year}</td>
                                            <td>{score.note || "-"}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    ) : (
                        <div className="sd-no-data">
                            Chưa có dữ liệu điểm chuẩn
                        </div>
                    )}
                </section>
            );

        default:
            return null;
    }
};

export default SchoolDetailPage;
