import React, { useState, useEffect } from 'react';
import '../styles/pages/search-scores.css';
import { admissionScoreService } from '../services/admissionScoreService';

const SearchScoresPage = () => {
    const [scores, setScores] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchScores = async () => {
            try {
                setLoading(true);
                const response = await admissionScoreService.getAll();
                setScores(response.data || []);
            } catch (error) {
                console.error('Error:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchScores();
    }, []);

    return (
        <div className="ss-page">
            <section className="search-header">
                <h1>Tra cứu điểm chuẩn</h1>
                <p>Thông tin điểm chuẩn các trường đại học</p>
            </section>

            <div className="ss-content">
                {loading ? (
                    <div>Đang tải dữ liệu...</div>
                ) : (
                    <table className="ss-scores-table">
                        <thead>
                            <tr>
                                <th>Trường</th>
                                <th>Mã ngành</th>
                                <th>Khối thi</th>
                                <th>Điểm chuẩn</th>
                            </tr>
                        </thead>
                        <tbody>
                            {scores.map((score, index) => (
                                <tr key={index}>
                                    <td>{score.university}</td>
                                    <td>{score.major_code}</td>
                                    <td>{score.subject_group}</td>
                                    <td className="score-value">{score.score}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default SearchScoresPage;
