                    onKeyPress={handleSearch}
                />
                <button className="search-icon"><Link to="/search">üîç</Link></button>
            </div>

            <ul>
                <li><Link to="/">Trang ch·ªß</Link></li>
                <li><Link to="/news">Tin t·ª©c</Link></li>
                <li className="dropdown" ref={dropdownRef}>
                    <span className="dropdown-trigger" onClick={() => setShowDropdown(!showDropdown)}>
                        Danh m·ª•c
                    </span>
                    <div className={`dropdown-menu ${showDropdown ? 'active' : ''}`}>
                        <Link to="/search/schools" onClick={() => setShowDropdown(false)}>Tr∆∞·ªùng h·ªçc</Link>
                        <Link to="/search/majors" onClick={() => setShowDropdown(false)}>Ng√†nh h·ªçc</Link>
